CREATE ALGORITHM=UNDEFINED DEFINER=`u766359255_raizes`@`%` SQL SECURITY DEFINER VIEW `icnt_vw_total_per_category` AS select `C`.`id` AS `id`,`C`.`categoria` AS `categoria`,count(`P`.`id`) AS `TOTAL_PONTOS` from ((`icnt_categoria_linha` `C` join `icnt_linha` `L` on(`L`.`categoria` = `C`.`id`)) join `icnt_pontos` `P` on(`P`.`linha` = `L`.`id`)) group by `L`.`categoria` desc order by count(`P`.`id`) desc,`L`.`linha`